const n=document.querySelectorAll(".profile .profile__content .manga__list .manga__item .shortcut__remove");n&&n.forEach(c=>{c.addEventListener("click",async function(o){o.preventDefault();const e=o.target.getAttribute("data-manga-id"),r=o.target.getAttribute("data-user-id");if(e==null||r==null||e==""||r=="")return Toastify({text:"Lo siento, ID Requerido",className:"error",duration:5e3,newWindow:!0,close:!0,gravity:"top",position:"center"}).showToast(),!0;await axios.post("/u/remove_shortcut",{manga_id:e,user_id:r},{headers:{"X-CSRF-TOKEN":document.querySelector('meta[name="csrf-token"]').getAttribute("content")}}).then(function(t){if(t.data.status=="success"){Toastify({text:t.data.message,className:"success",duration:1e3,newWindow:!0,close:!0,gravity:"top",position:"center"}).showToast();const a=document.querySelector("#shortcut-"+e),s=document.querySelector("#shortcut-p-"+e);a&&a.remove(),s&&s.remove()}t.data.status=="error"&&Toastify({text:t.data.message,className:"error",duration:5e3,newWindow:!0,close:!0,gravity:"top",position:"center"}).showToast()}).catch(function(t){console.log("error: ",t)})})});
