const n=document.querySelectorAll(".profile .profile__content .manga__list .manga__item .shortcut__remove");n&&n.forEach(c=>{c.addEventListener("click",async function(r){r.preventDefault();const t=r.target.getAttribute("data-manga-id"),o=r.target.getAttribute("data-user-id");if(console.log(t==null),console.log(o),t==null||o==null||t==""||o=="")return Toastify({text:"Lo siento, ID Requerido",className:"error",duration:5e3,newWindow:!0,close:!0,gravity:"top",position:"center"}).showToast(),!0;await axios.post("/u/remove_shortcut",{manga_id:t,user_id:o},{headers:{"X-CSRF-TOKEN":document.querySelector('meta[name="csrf-token"]').getAttribute("content")}}).then(function(e){if(e.data.status=="success"){Toastify({text:e.data.message,className:"success",duration:1e3,newWindow:!0,close:!0,gravity:"top",position:"center"}).showToast();const a=document.querySelector("#shortcut-"+t),s=document.querySelector("#shortcut-p-"+t);a&&a.remove(),s&&s.remove()}e.data.status=="error"&&Toastify({text:e.data.message,className:"error",duration:5e3,newWindow:!0,close:!0,gravity:"top",position:"center"}).showToast()}).catch(function(e){console.log("error: ",e)})})});
